import { useState } from "react";

// Expanded and richer nutrition plan data
const nutritionPlans = {
  underweight: {
    description: "Focus on calorie-dense and nutritious foods to promote healthy weight gain.",
    ingredients: [
      "Whole grains (brown rice, oats, millet)",
      "Dairy (milk, cheese, yogurt)",
      "Avocados, nut butters (peanut, almond)",
      "Lean meats, chicken, and eggs",
      "Nuts and seeds (almonds, chia, flaxseeds)",
      "Smoothies with banana, milk, and oats",
      "Starchy vegetables (potatoes, sweet potatoes)",
      "Healthy oils (olive, coconut)",
      "Dried fruits and energy bars",
      "Paneer and tofu for plant-based protein"
    ],
    recipes: [
      "Peanut Butter Banana Smoothie",
      "Avocado and Egg Toast",
      "Whole-Grain Pasta with Cream Sauce",
      "Oats Bowl with Milk and Mixed Nuts",
      "Paneer Stir-Fry with Quinoa"
    ]
  },
  normal: {
    description: "Maintain balanced nutrition with lean proteins and whole foods.",
    ingredients: [
      "Fresh vegetables—broccoli, spinach, carrots, peas",
      "Whole grains (quinoa, oats, brown rice)",
      "Fruits (berries, apples, oranges, bananas)",
      "Lean proteins (fish, chicken, tofu, eggs)",
      "Legumes (lentils, beans, chickpeas)",
      "Low-fat dairy products",
      "Healthy fats (olive oil, avocado, nuts)",
      "Hydration: water, green tea, herbal infusions"
    ],
    recipes: [
      "Grilled Chicken and Mixed Veggie Bowl",
      "Veggie Lentil Soup",
      "Oatmeal with Fruits and Seeds",
      "Quinoa Chickpea Salad",
      "Boiled Egg and Veg Wrap"
    ]
  },
  overweight: {
    description: "Focus on fiber and protein-rich foods with limited fats and carbs.",
    ingredients: [
      "Leafy greens (spinach, kale)",
      "Cruciferous vegetables (broccoli, cauliflower)",
      "Whole fruits (berries, apples)",
      "Oats and barley for slow-digesting carbs",
      "Lean proteins (fish, tofu, egg whites)",
      "Legumes and lentils",
      "Low-fat dairy (Greek yogurt, skimmed milk)",
      "Green tea, lemon water, and detox drinks"
    ],
    recipes: [
      "Green Detox Smoothie",
      "Grilled Fish with Sautéed Veggies",
      "Vegetable Lentil Stir Fry",
      "Oats Upma with Spinach",
      "Roasted Chickpeas Salad"
    ]
  },
  obese: {
    description: "Focus on high-fiber, low-calorie, and low-fat meals for sustainable weight loss.",
    ingredients: [
      "Vegetables (broccoli, cabbage, carrots, beans)",
      "Whole fruits (papaya, oranges, pomegranate)",
      "Millets and quinoa (in small portions)",
      "Legumes and pulses (chickpeas, beans, lentils)",
      "Lean proteins (chicken breast, fish, egg whites)",
      "Low-fat dairy (skim milk, yogurt)",
      "Green tea, lemon water",
      "Healthy spices (turmeric, cinnamon, ginger)",
      "Soups and clear broths (minimal oil)",
      "Mixed salads with flaxseeds"
    ],
    recipes: [
      "Vegetable and Lentil Soup",
      "Quinoa Salad with Spinach",
      "Grilled Chicken with Vegetable Broth",
      "Oats Idli with Fresh Chutneys",
      "Sprout Salad with Lemon Dressing"
    ]
  }
};

function YoutubeEmbed({ videoId }) {
  return (
    <iframe
      width="320"
      height="180"
      src={`https://www.youtube.com/embed/${videoId}`}
      frameBorder="0"
      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
      allowFullScreen
      title="YouTube video"
      style={{ margin: "10px 0" }}
    />
  );
}

export default function ChatCoach() {
  const [category, setCategory] = useState("");
  const [messages, setMessages] = useState([]);
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [input, setInput] = useState("");
  const [videoIds, setVideoIds] = useState([]);
  const [bmi, setBmi] = useState(null);
  const [bmiStatus, setBmiStatus] = useState("");

  async function fetchVideos(query) {
    try {
      const res = await fetch("http://localhost:3001/videos", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query })
      });
      const data = await res.json();
      return data.titles || [];
    } catch {
      return [];
    }
  }

  function getBMIStatus(bmi) {
    if (bmi < 18.5) return "underweight";
    if (bmi < 25) return "normal";
    if (bmi < 30) return "overweight";
    return "obese";
  }

  async function handleMessageSend(e) {
    e.preventDefault();
    if (!input) return;
    setMessages(msgs => [...msgs, { sender: "user", text: input }]);

    if (category === "bmi") {
      if (!height) {
        setHeight(input);
        setMessages(msgs => [...msgs, { sender: "bot", text: "Please enter your weight (kg):" }]);
        setInput("");
        return;
      }
      if (!weight) {
        setWeight(input);
        const heightVal = parseFloat(height) / 100;
        const weightVal = parseFloat(input);
        const bmiValue = weightVal / (heightVal * heightVal);
        const status = getBMIStatus(bmiValue);
        setBmi(bmiValue.toFixed(2));
        setBmiStatus(status);
        setMessages(msgs => [
          ...msgs,
          { sender: "bot", text: `Your BMI is ${bmiValue.toFixed(2)}. You are ${status}.` }
        ]);
        setInput("");
        return;
      }
    }

    if (category === "nutrition") {
      const status = bmiStatus || input.trim().toLowerCase();
      if (!nutritionPlans[status]) {
        setMessages(msgs => [
          ...msgs,
          { sender: "bot", text: "Enter valid BMI status: underweight, normal, overweight, or obese." }
        ]);
        setInput("");
        return;
      }

      const plan = nutritionPlans[status];
      const vids = await fetchVideos(status + " nutrition diet");

      const variedIngredients =
        bmi && +bmi > 30
          ? [...plan.ingredients, "Increase fiber-rich soups", "Drink warm water with lemon daily", "Avoid fried foods"]
          : bmi && +bmi < 18.5
          ? [...plan.ingredients, "Add protein shakes twice daily", "Include nuts and nut butter snacks", "Use olive oil generously"]
          : plan.ingredients;

      setVideoIds(vids);
      setMessages(msgs => [
        ...msgs,
        { sender: "bot", text: plan.description },
        { sender: "bot", text: `Ingredients: ${variedIngredients.join(", ")}` },
        { sender: "bot", text: `Recipes: ${plan.recipes.join(", ")}` },
        { sender: "bot", text: "Here are some related nutrition videos:" }
      ]);
      setInput("");
      return;
    }

    if (category === "workout") {
      const choice = input.trim().toLowerCase();
      const vids = await fetchVideos(choice + " workout");
      setVideoIds(vids);
      setMessages(msgs => [...msgs, { sender: "bot", text: "Here are some workout videos:" }]);
      setInput("");
      return;
    }

    setInput("");
  }

  function handleCategorySelect(cat) {
    setCategory(cat);
    setMessages([]);
    setHeight("");
    setWeight("");
    setInput("");
    setVideoIds([]);
    if (cat === "bmi") {
      setMessages([{ sender: "bot", text: "Please enter your height (cm):" }]);
    } else if (cat === "nutrition") {
      setMessages([{ sender: "bot", text: "Please enter your BMI status or calculate BMI first (e.g., underweight, normal, overweight, obese):" }]);
    } else if (cat === "workout") {
      setMessages([{ sender: "bot", text: "What type of workout are you interested in? (e.g., cardio, strength, flexibility, burnout)" }]);
    }
  }

  const buttonStyle = {
    padding: "15px 30px",
    margin: "10px",
    border: "none",
    borderRadius: "8px",
    color: "white",
    fontSize: "18px",
    cursor: "pointer",
    transition: "background-color 0.3s ease"
  };

  const categories = [
    { label: "BMI Calculator", color: "#4CAF50", key: "bmi" },
    { label: "Nutrition Diet Planner", color: "#2196F3", key: "nutrition" },
    { label: "Workout/Burnout", color: "#FF9800", key: "workout" }
  ];

  return (
    <div style={{ textAlign: "center", padding: "1.5em", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ fontSize: "2.5em", color: "#333", marginBottom: "1em" }}>AI Fitness Coach Chatbot</h1>

      <div>
        {categories.map(cat => (
          <button
            key={cat.key}
            onClick={() => handleCategorySelect(cat.key)}
            style={{ ...buttonStyle, backgroundColor: cat.color }}
            onMouseOver={e => (e.target.style.opacity = 0.9)}
            onMouseOut={e => (e.target.style.opacity = 1)}
          >
            {cat.label}
          </button>
        ))}
      </div>

      <div
        style={{
          margin: "1.5em auto",
          width: "80%",
          maxWidth: "600px",
          minHeight: "250px",
          border: "1px solid #ddd",
          borderRadius: "8px",
          padding: "1em",
          backgroundColor: "#fafafa",
          textAlign: "left",
          overflowY: "auto"
        }}
      >
        {messages.map((msg, idx) => (
          <div key={idx} style={{ textAlign: msg.sender === "user" ? "right" : "left", margin: "8px 0" }}>
            <span
              style={{
                display: "inline-block",
                padding: "8px 12px",
                borderRadius: "6px",
                backgroundColor: msg.sender === "user" ? "#DCF8C6" : "#E8EAF6"
              }}
            >
              {msg.text}
            </span>
          </div>
        ))}
        {videoIds.length > 0 && videoIds.map((id, index) => <YoutubeEmbed videoId={id} key={index} />)}
      </div>

      {category !== "" && (
        <form onSubmit={handleMessageSend}>
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Type your reply..."
            style={{
              padding: "10px",
              width: "60%",
              borderRadius: "5px",
              border: "1px solid #ccc",
              marginRight: "10px",
              fontSize: "16px"
            }}
          />
          <button
            type="submit"
            style={{ ...buttonStyle, backgroundColor: "#673AB7", fontSize: "16px", padding: "10px 20px" }}
          >
            Send
          </button>
        </form>
      )}
    </div>
  );
}
