// src/ChatCoach.js
import { useState } from "react";

const nutritionPlans = {
  underweight: {
    ingredients: ["Whole grains", "Lean meats", "Nuts", "Avocado", "Greek yogurt"],
    recipes: ["Avocado Toast", "Protein Smoothie", "Chicken and Rice Bowl"]
  },
  normal: {
    ingredients: ["Brown rice", "Broccoli", "Grilled Chicken", "Sweet potato", "Eggs"],
    recipes: ["Grilled Chicken Salad", "Egg & Veggie Wrap", "Broccoli Stir Fry"]
  },
  overweight: {
    ingredients: ["Oats", "Spinach", "Fish", "Berries", "Cottage cheese"],
    recipes: ["Berry Oatmeal", "Spinach Omelette", "Baked Fish"]
  },
  obese: {
    ingredients: ["Leafy greens", "Lean turkey", "Quinoa", "Beans", "Tomatoes"],
    recipes: ["Quinoa Salad", "Turkey Lettuce Wraps", "Bean Soup"]
  }
};

function YoutubeEmbed({ videoId }) {
  return (
    <iframe
      width="320"
      height="180"
      src={`https://www.youtube.com/embed/${videoId}`}
      frameBorder="0"
      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
      allowFullScreen
      title="YouTube video"
      style={{ margin: "10px 0" }}
    />
  );
}

export default function ChatCoach() {
  const [category, setCategory] = useState("");
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [step, setStep] = useState(0); // tracks BMI or Nutrition steps
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [bmiStatus, setBmiStatus] = useState("");
  const [dietType, setDietType] = useState("");
  const [days, setDays] = useState(0);
  const [videoIds, setVideoIds] = useState([]);

  async function fetchVideos(query) {
    try {
      const res = await fetch("http://localhost:3001/videos", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query })
      });
      const data = await res.json();
      return data.titles || [];
    } catch {
      return [];
    }
  }

  function getBMIStatus(bmi) {
    if (bmi < 18.5) return "underweight";
    if (bmi < 25) return "normal";
    if (bmi < 30) return "overweight";
    return "obese";
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setMessages((msgs) => [...msgs, { sender: "user", text: input }]);

    // -------- BMI Logic --------
    if (category === "bmi") {
      if (step === 0) {
        setHeight(input);
        setMessages((msgs) => [...msgs, { sender: "bot", text: "Please enter your weight (kg):" }]);
        setInput("");
        setStep(1);
        return;
      } else if (step === 1) {
        setWeight(input);
        const h = parseFloat(height) / 100;
        const w = parseFloat(input);
        const bmi = w / (h * h);
        const status = getBMIStatus(bmi);
        setMessages((msgs) => [
          ...msgs,
          { sender: "bot", text: `Your BMI is ${bmi.toFixed(2)}. You are ${status}.` }
        ]);
        setHeight(""); setWeight(""); setStep(0); setInput("");
        return;
      }
    }

    // -------- Nutrition Logic --------
    if (category === "nutrition") {
      if (step === 0) {
        const status = input.toLowerCase();
        if (!nutritionPlans[status]) {
          setMessages((msgs) => [...msgs, { sender: "bot", text: "Enter valid BMI status: underweight, normal, overweight, obese." }]);
          setInput("");
          return;
        }
        setBmiStatus(status);
        setMessages((msgs) => [...msgs, { sender: "bot", text: "Do you prefer veg or nonveg?" }]);
        setStep(1);
        setInput("");
        return;
      } else if (step === 1) {
        const type = input.toLowerCase();
        if (!["veg", "nonveg"].includes(type)) {
          setMessages((msgs) => [...msgs, { sender: "bot", text: "Enter 'veg' or 'nonveg'." }]);
          setInput("");
          return;
        }
        setDietType(type);
        setMessages((msgs) => [...msgs, { sender: "bot", text: "How many days of diet plan do you want?" }]);
        setStep(2);
        setInput("");
        return;
      } else if (step === 2) {
        const d = parseInt(input);
        if (isNaN(d) || d < 1) {
          setMessages((msgs) => [...msgs, { sender: "bot", text: "Enter a valid number of days." }]);
          setInput("");
          return;
        }
        setDays(d);
        const plan = nutritionPlans[bmiStatus];
        let dietMsg = `Hereâ€™s your ${d}-day ${dietType} diet plan for ${bmiStatus} BMI:\n`;
        for (let i = 1; i <= d; i++) {
          dietMsg += `Day ${i}: Ingredients: ${plan.ingredients.join(", ")}, Recipe: ${plan.recipes[i % plan.recipes.length]}\n`;
        }
        const vids = await fetchVideos(`${bmiStatus} ${dietType} diet`);
        setVideoIds(vids);
        setMessages((msgs) => [...msgs, { sender: "bot", text: dietMsg }, { sender: "bot", text: "Here are some videos:" }]);
        setStep(0); setInput(""); setBmiStatus(""); setDietType(""); setDays(0);
        return;
      }
    }

    // -------- Workout Logic --------
    if (category === "workout") {
      const vids = await fetchVideos(input + " workout");
      setVideoIds(vids);
      setMessages((msgs) => [...msgs, { sender: "bot", text: "Here are some workout videos:" }]);
      setInput("");
      return;
    }

    setInput("");
  };

  const handleCategorySelect = (cat) => {
    setCategory(cat);
    setMessages([]);
    setInput("");
    setStep(0);
    setHeight("");
    setWeight("");
    setBmiStatus("");
    setDietType("");
    setDays(0);
    setVideoIds([]);
    if (cat === "bmi") setMessages([{ sender: "bot", text: "Please enter your height (cm):" }]);
    if (cat === "nutrition") setMessages([{ sender: "bot", text: "Please enter your BMI status (underweight, normal, overweight, obese):" }]);
    if (cat === "workout") setMessages([{ sender: "bot", text: "Enter the type of workout you want (e.g., cardio, strength):" }]);
  };

  return (
    <div style={{ padding: "1em" }}>
      <h2>ðŸ’ª AI Fitness Coach Chatbot</h2>
      <div style={{ marginBottom: "1em" }}>
        <button onClick={() => handleCategorySelect("bmi")}>BMI Calculator</button>
        <button onClick={() => handleCategorySelect("nutrition")}>Nutrition Diet Planner</button>
        <button onClick={() => handleCategorySelect("workout")}>Workout</button>
      </div>

      <div style={{ border: "1px solid #ccc", minHeight: "250px", padding: "1em", marginBottom: "1em" }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{ textAlign: msg.sender === "user" ? "right" : "left" }}>
            <span>{msg.sender === "bot" ? "ðŸ¤–" : "ðŸ§‘"} {msg.text}</span>
          </div>
        ))}
        {videoIds.length > 0 && videoIds.map((id, index) => <YoutubeEmbed key={index} videoId={id} />)}
      </div>

      {category && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your reply..."
          />
          <button type="submit">Send</button>
        </form>
      )}
    </div>
  );
}
